import Number from '..';

describe('Number.min', () => {
  it('should check that the value is greater or equal minimum', async () => {
    const validator = new Number();

    validator.min(5);

    const result1 = await validator.validate(0);
    const result2 = await validator.validate(5);
    const result3 = await validator.validate(10);

    expect(result1).toEqual({ id: 1, result: 5 });
    expect(result2).toBeNull();
    expect(result3).toBeNull();
  });
});
