import Number from '..';

describe('Number.min', () => {
  it('should check that the value is greater or equal minimum', async () => {
    const validator = new Number();

    validator.min(5);

    const res1 = await validator.validate(0);
    const res2 = await validator.validate(5);
    const res3 = await validator.validate(10);

    expect(res1).toBe('must be greater or equal to 5');
    expect(res2).toBe(null);
    expect(res3).toBe(null);
  });
});
